import React, { useState, useContext } from "react";

import { GameContext } from "./store/game-context";

import { ReactComponent as Settings } from "./assets/settings.svg";

import Modal from "./components/Modals/Modal";

import styles from "./App.module.scss";
import CardBoard from "./components/CardBoard/CardBoard";
import Player from "./components/Player/Player";
import NewPlayer from "./components/Modals/NewPlayer/NewPlayer";

function App() {
  const { finishGame } = useContext(GameContext);
  const [showNewPlayer, setShowNewPlayer] = useState(true);

  if (finishGame) {
    return (
      <div>
        <h1>KONIEC GRY</h1>
      </div>
    );
  }

  return (
    <div className={styles.app}>
      {showNewPlayer && <NewPlayer setShowNewPlayer={setShowNewPlayer} />}
      <Settings
        fill="white"
        stroke="white"
        strokeWidth="15"
        className={styles.settingsBtn}
      />
      <div className={styles.game}>
        <Player />
        <CardBoard />
      </div>
    </div>
  );
}

export default App;
